mapper.select:
select * from orders where user_id = #{userId}
and  status = #{status}
order by order_time desc

select * from orders


    and order_time >= #{beginTime} and order_time <=  #{endTime} 
    and number like concat('%',#{number},'%') 
    and phone like concat('%',#{phone},'%') 
    and status = #{status}

order by order_time desc

select sum(amount)
from orders
where order_time >= #{begin}
and order_time < #{end}
and status = 5
